import React from "react";
import portrait from "./eden.png";
import { Link } from "react-router-dom";
import outboundBlack from "./outbound_black.png";
function About(props) {
  return (
    <div id="About" className="layout">
      <h1>Eden Fuller</h1>
      <h2>all about me</h2>
      <div class="about-content">
        <p>
          <strong>Eden Fuller</strong> is a front-end web engineer with 8 years'
          experience. Eden is currently the lead storefront engineer at Trove.
          She's also worked at IKEA, AOL, and Brinkbit.
        </p>
        <p>
          <Link
            to="/novel"
            className="about-link"
            onClick={() => {
              props.setRoute("novel");
            }}
          >
            <em>The Dweller's House</em>
          </Link>{" "}
          is Eden's current project.
        </p>
        <p>
          Eden's{" "}
          <Link
            to="/stories"
            className="about-link"
            onClick={() => {
              props.setRoute("stories");
            }}
          >
            short stories
          </Link>{" "}
          have appeared or are forthcoming in <em>Nature Futures</em>,{" "}
          <em>Daily Science Fiction</em>, and{" "}
          <em>Asimov's Science Fiction Magazine</em>.
        </p>
        <p>
          While studying at Temple University, Eden started the writing tips
          blog{" "}
          <a href="http://prosepro.tips">
            <span className="about-link">
              Prose Protips <img src={outboundBlack}></img>
            </span>
          </a>{" "}
          and wrote two young adult dystopian novels,{" "}
          <a href="https://www.goodreads.com/book/show/13176257-mutt">
            <span className="about-link">
              <em>Mutt</em> <img src={outboundBlack}></img>
            </span>
          </a>{" "}
          and{" "}
          <a href="https://www.goodreads.com/book/show/17407180-stray">
            <span className="about-link">
              <em>Stray</em> <img src={outboundBlack}></img>
            </span>
          </a>
          . Thanks to dozens of book bloggers on Tumblr, these self-published
          books won over 9,000 readers.
        </p>
        <p>
          <strong>
            <em>let's create together //</em>
          </strong>
          <br />
          <a href="mailto:fuller.eden.m@gmail.com">
            <span className="about-link">
              email <img src={outboundBlack}></img>
            </span>
          </a>
          <br />{" "}
          <a href="https://linkedin.com/in/edenfuller">
            <span className="about-link">
              linkedin <img src={outboundBlack}></img>
            </span>
          </a>
        </p>
      </div>
      <img
        src={portrait}
        className="portrait small"
        alt="photograph of Eden Fuller"
      />
    </div>
  );
}
export default About;
